<?phpclass Insync_Supplieradd_Model_Supplieradd_Api_V2  {	public function supp($supplierData) {		$tempArray = (array)$supplierData;		$tempId = $tempArray['sup_id'];		$tempCode = $tempArray['sup_code'];		$tempName = $tempArray['sup_name'];		$tempEmail = $tempArray['sup_email'];		$tempTel = $tempArray['sup_tel'];		$id = '';		if($tempId == ''){			$model = Mage::getModel('supplier/supplier');					$model->setData('name',$tempCode);			$model->setData('sup_name',$tempName);			$model->setData('sup_email',$tempEmail);			$model->setData('sup_tel',$tempTel);			$model->save();			$id = $model->getId();		}else{			$model = Mage::getModel('supplier/supplier')->load($tempId);			$model->setData('name',$tempCode);			$model->setData('sup_name',$tempName);			$model->setData('sup_email',$tempEmail);			$model->setData('sup_tel',$tempTel);			$model->save();			$id = $tempId ;		}		return $id ;    }}